mapper.select:
SELECT m.m_id
FROM member_info m
JOIN employee_info e ON m.m_key = e.m_key
WHERE e.e_name = #{e_name}
  AND e.e_tel_num = #{e_tel_num}
  AND e.e_birth = #{e_birth};

SELECT m.m_pw
FROM member_info m
JOIN employee_info e ON m.m_key = e.m_key
WHERE m.m_id = #{m_id}
  AND e.e_tel_num = #{e_tel_num};
