mapper.select:
SELECT e.e_num, e.e_name, e.e_birth, e.e_level, e.e_tel_num, e.e_key, e.e_team, v.vacation_id
FROM employee_info e
JOIN vacation v ON e.e_key = v.e_key;

mapper.delete:
	    DELETE FROM member_info 
    WHERE e_key IN (
        SELECT e_key FROM employee_info 
        WHERE e_num IN

            #{eNums}

    );

    DELETE FROM vacation 
    WHERE e_key IN (
        SELECT e_key FROM employee_info 
        WHERE e_num IN

            #{eNums}

    );

	   DELETE FROM employee_info 
    WHERE e_num IN

        #{eNums}
